import {Component} from "react";
import Statistics from '../components/Statistics';
import FeedbackOptions from '../components/FeedbackOptions';

class App extends Component {
  
  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  addStatisticValue = prop => {
    this.setState(prevState => ({
      [prop]: prevState[prop] + 1
    }))
  }

  countTotalFeedback = () => {
    return Object.values(this.state).reduce((acc, prop) => acc + prop, 0 ); 
  }

  countPositiveFeedbackPercentage() {
    const{ good } = this.state;
    return Math.round((good * 100) / this.countTotalFeedback()) || 0;
  } 

  render() {

    const { good, neutral, bad} = this.state;
    const total = this.countTotalFeedback;
    const positiveFeedbackPercentage = this.countPositiveFeedbackPercentage;

    return (
      <div
        style={{
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: 40,
          color: '#010101'
        }}
      >
        <FeedbackOptions options={Object.keys.this.state} onLeaveFeedback={this.addStatisticValue}/>
        <Statistics 
          good={good} 
          neutral={neutral} 
          bad={bad} 
          total={total} 
          positiveFeedbackPercentage={positiveFeedbackPercentage}/>
      </div>   
    );
  }
};

export default App;
